@import "./normalize.css";

/* Color Pallete */
:root  {
  --color-primary: #2584ff;
  --color-secondary: #00d9ff;
  --color-accent: #ff3400;
  --color-headings: #1b0760;
  --color-body: #918ca4;
  --color-body-darker: #5c5577;
  --color-border: #ccc;
  --border-radius: 30px;
}
*,
*::after,
*::before {
  box-sizing: border-box;
}
/* Typography */
::selection {
  background: var(--color-primary);
  color: white;
}

html {
  font-size: 62.5%;
}
body {
  font-family: Inter, Arial, Helvetica, sans-serif;
  font-size: 2.4rem;
  line-height: 1.5;
  color: var(--color-body);
}
h1,
h2,
h3 {
  color: var(--color-headings);
  margin-bottom: 1rem;
  line-height: 1.1;
}
p {
  margin-top: 0;
}

h1 {
  font-size: 7rem;
}
h2 {
  font-size: 4rem;
}
h3 {
  font-size: 3rem;
  font-weight: 500;
}
@media screen and (min-width: 1024px) {
  body {
    font-size: 1.8rem;
  }
  h1 {
    font-size: 8rem;
  }
  h2 {
    font-size: 4rem;
  }
  h3 {
    font-size: 2.4rem;
  }
}

/* Links */
a {
  text-decoration: none;
}

.link-arrow {
  color: var(--color-accent);
  text-transform: uppercase;
  font-size: 2rem;
  font-weight: bold;
}

.link-arrow::after {
  content: "-->";
  margin-left: 5px;
  transition: margin 0.15s;
}
.link-arrow:hover::after {
  margin-left: 10px;
}

@media screen and (min-width: 1024px) {
  .link-arrow {
    font-size: 1.5rem;
  }
}

/* Badges */

.badge {
  border-radius: 20px;
  font-size: 2rem;
  font-weight: 600;
  padding: 0.5rem 2rem;
  white-space: nowrap;
}

.badge--primary {
  background: var(--color-primary);
  color: white;
}

.badge--secondary {
  background: var(--color-secondary);
  color: white;
}

.badge--small {
  font-size: 1.6rem;
  padding: 0.5rem 1.5rem;
}

@media screen and (min-width: 1024px) {
  .badge {
    font-size: 1.5rem;
  }
  .badge--small {
    font-size: 1.2rem;
  }
}

/* Lists */

.list {
  font-size: 2.4rem;
  list-style: none;
  padding-left: 0;
}

.list--inline .list__item {
  display: inline-block;
  margin-right: 2rem;
}

.list--check {
  list-style-image: url(../Images/tick.svg);
  padding-left: 3rem;
  color: var(--color-headings);
}

.list--check .list__item {
  padding-left: 0.5rem;
  margin-bottom: 1rem;
}
@media screen and (min-width: 1024px) {
  .list--check .list__item {
    padding-left: 0;
  }
}

/* Icons */

.icon {
  width: 40px;
  height: 40px;
}
.icon--small {
  width: 30px;
  height: 30px;
}

.icon--primary {
  fill: var(--color-primary);
}
.icon--white {
  fill: white;
}

.icon-container {
  align-items: center;
  background: #f3f9fa;
  border-radius: 100%;
  display: inline-flex;
  height: 64px;
  justify-content: center;
  width: 64px;
}

.icon-container--accent {
  background: var(--color-accent);
}

/* Buttons */

.btn {
  border-radius: var(--border-radius);
  border: none;
  cursor: pointer;
  font-size: 1.8rem;
  outline: 0;
  font-weight: 700;
  padding: 2rem 4vw;
  text-align: center;
  text-transform: uppercase;
  white-space: nowrap;
  margin: 1rem 0;
}
.btn--block {
  width: 100%;
  display: inline-block;
}
.btn--stretched {
  padding-left: 6rem;
  padding-right: 6rem;
}
.btn--outline {
  border: 3px solid var(--color-headings);
  color: var(--color-headings);
  background: white;
}
.btn--primary {
  background: var(--color-headings);
  color: white;
}
.btn--secondary {
  background: var(--color-secondary);
  color: white;
}
.btn--accent {
  background: var(--color-accent);
  color: white;
}
.btn--outline:hover {
  background: var(--color-headings);
  color: #f3f9fa;
}
.btn--primary:hover {
  background: #0c8ceb;
}
.btn--secondary:hover {
  background: #00c8eb;
}
.btn--accent:hover {
  background: #fd4e21;
}
.btn .icon {
  width: 2rem;
  height: 2rem;
  margin-right: 1rem;
  vertical-align: middle;
}

@media screen and (min-width: 1024px) {
  .btn {
    font-size: 1.5rem;
  }
}

/* Inputs */

.input {
  border-radius: var(--border-radius);
  border: 1px solid var(--color-border);
  color: var(--color-headings);
  font-size: 2rem;
  outline: 0;
  padding: 1.5rem 3.5rem;
}
::placeholder {
  color: #cdcbd7;
}
.input-group {
  border: 1px solid var(--color-border);
  border-radius: var(--border-radius);
  display: flex;
  box-shadow: 0 0 10px var(--color-border);
}
.input-group .input {
  border: 0;
  flex-grow: 1;
  padding: 1.5rem 2rem;
  width: 0;
}
.input-group .btn {
  margin: 4px;
}

@media screen and (min-width: 1024px) {
  .input {
    font-size: 1.5rem;
  }
}

/* Card */

.card {
  border-radius: 7px;
  box-shadow: 0 0 20px 10px #f3f3f3;
  overflow: hidden;
}

.card__header,
.card__body {
  padding: 3rem 4rem;
}

.card--primary .card__header {
  background: var(--color-primary);
  color: white;
}
.card--secondary .card__header {
  background: var(--color-secondary);
  color: white;
}
.card--secondary .badge--secondary {
  background: #02cdf1;
}
.card--primary .badge--primary {
  background: #126de4;
}
/* Plans */

.plan__name {
  color: white;
  margin: 0;
  font-weight: 500;
  font-size: 2.4rem;
}
.plan__price {
  font-size: 6rem;
}
.plan__billing-cycle {
  font-size: 2.4rem;
  font-weight: 300;
  opacity: 0.8;
  margin-right: 1rem;
}
.plan__description {
  font-size: 2rem;
  font-weight: 300;
  letter-spacing: 1px;
  display: block;
}
.plan .list__item {
  margin-bottom: 2rem;
}
.plan--popular .card__header {
  position: relative;
}
.plan--popular {
  transform: scale(1.1);
}
.plan {
  transition: transform 0.2s ease-out;
}
.plan:hover {
  transform: scale(1.05);
}
.plan--popular:hover {
  transform: scale(1.15);
}
.plan--popular .card__header::before {
  content: url(../Images/popular.svg);
  width: 40px;
  display: inline-block;
  position: absolute;
  top: -6px;
  right: 5%;
}
@media screen and (min-width: 1024px) {
  .plan__name {
    font-size: 1.4rem;
  }
  .plan__price {
    font-size: 5rem;
  }
  .plan__billing-cycle {
    font-size: 1.6rem;
  }
  .plan__discription {
    font-size: 1.7rem;
  }
}

/* Media */

.media {
  display: flex;
  margin-bottom: 4rem;
}

.media__title {
  margin-top: 0;
}
.media__body {
  margin: 0 2rem;
}
.media__image {
  margin-top: 1rem;
}

/* Quote */

.quote {
  font-size: 3rem;
  font-style: italic;
  color: var(--color-body-darker);
  line-height: 1.3;
}
.quote__text::before {
  content: open-quote;
}
.quote__text::after {
  content: close-quote;
}

.quote__body {
  font-style: normal;
}
.quote__author {
  letter-spacing: 2px;
  margin-bottom: 0;
}
.quote__company {
  font-size: 2rem;
  color: var(--color-headings);
  opacity: 0.4;
}
.quote__line {
  position: relative;
  bottom: 10px;
}

@media screen and (min-width: 1024px) {
  .quote {
    font-size: 2rem;
  }
  .quote__author {
    font-size: 2.4rem;
  }
  .quote__company {
    font-size: 1.6rem;
  }
}

/* Grids */

.grid {
  display: grid;
}
@media screen and (min-width: 768px) {
  .grid--1x2 {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media screen and (min-width: 1024px) {
  .grid--1x3 {
    grid-template-columns: repeat(3, 1fr);
  }
}

/* Testimonials */

.testimonial {
  padding: 0.5rem;
}
.testimonial__image {
  position: relative;
  margin: 3rem 5.5rem;
}
.testimonial__image > img {
  width: 100%;
}
.testimonial__image > .icon-container {
  position: absolute;
  top: 3rem;
  right: -32px;
}

@media screen and (min-width: 768px) {
  .testimonial .quote,
  .testimonial .quote__author {
    font-size: 2.4rem;
  }
}
.testimonial .quote {
  margin-left: 6rem;
}

/* Callouts */

.callout {
  padding: 4rem;
  border-radius: 7px;
  font-size: 2.4rem;
}

.callout--primary {
  background: var(--color-primary);
  color: white;
}
.callout__title {
  color: white;
  margin-top: 0;
  font-size: 4rem;
}
.callout .btn {
  justify-self: center;
  align-self: center;
}
.callout__content {
  text-align: center;
}

@media screen and (min-width: 768px) {
  .callout .grid--1x2 {
    grid-template-columns: 1fr auto;
  }

  .callout__content {
    text-align: left;
  }
  .callout .btn {
    justify-self: start;
    margin: 0 2rem;
  }
}

/* Collapsible */

.collapsible__header {
  display: flex;
  justify-content: space-between;
}

.collapsible__heading {
  margin-top: 0;
  font-size: 3rem;
}
.collapsible__chevron {
  transform: rotate(-90deg);
  transition: transform 0.3s;
}
.collapsible__content {
  max-height: 0;
  overflow: hidden;
  opacity: 0;
  transition: all 0.3s;
}

.collapsible--expanded .collapsible__chevron {
  transform: rotate(0);
}
.collapsible--expanded .collapsible__content {
  max-height: 100vh;
  opacity: 1;
}

/* Blocks */
.block {
  --vertical-padding: 6rem;
  padding: var(--vertical-padding) 2rem;
}
.block__header {
  text-align: center;
  margin-bottom: 4rem;
}
.block--dark {
  background: black;
  color: lightgrey;
}
.block--dark h1,
.block--dark h2,
.block--dark h3,
.block--dark h4,
.block--dark h5,
.block--dark h6 {
  color: white;
}
.block--skewed-right {
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 90%);
  padding-bottom: calc(var(--vertical-padding) + 4rem);
}
.block--skewed-left {
  clip-path: polygon(0% 0%, 100% 0%, 100% 90%, 0% 100%);
  padding-bottom: calc(var(--vertical-padding) + 4rem);
}
.container {
  max-width: 1140px;
  margin: 0 auto;
}
/* Navigation Bar */

.nav {
  background: black;
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  padding: 0 1rem;
  align-items: center;
}
.nav__list {
  width: 100%;
  margin: 0;
}
.nav__item {
  padding: 1rem 2rem;
}
.nav__item > a {
  color: grey;
  transition: color 0.3s;
}
.nav__item > a:hover {
  color: white;
}
.nav__toggle {
  opacity: 0.7;
  border-radius: 50%;
  transition: all 0.15s;
}
.collapsible--expanded .nav__toggle {
  opacity: 1;
  box-shadow: 0 0 0 3px #666;
}
.nav__brand {
  transform: translateY(5px);
}

@media screen and (min-width: 768px) {
  .nav__toggle {
    display: none;
  }
  .nav__list {
    width: auto;
    display: flex;
    opacity: 1;
    max-height: 100vh;
    font-size: 1.6rem;
  }
}

/* Hero*/

.hero {
  padding-top: 0;
}
.hero__image > img {
  width: 100%;
}
.hero__content {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.hero__tagline {
  font-size: 2rem;
  letter-spacing: 2px;
  padding: 1rem 0 2rem;
}

@media screen and (min-width: 768px) {
  .hero__content {
    text-align: left;
    align-items: flex-start;
  }
}

/* Domain Block */

.block-domain .input-group {
  box-shadow: 0 0 30px 20px #e6ebee;
  border: 0;
  margin: 4rem auto;
  max-width: 670px;
}
.block-domain__prices {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 6rem);
  font-size: 2rem;
  font-weight: 600;
  justify-items: center;
  max-width: 700px;
  margin: 0 auto;
  color: var(--color-headings);
}

@media screen and (min-width: 768px) {
  .block-domain__prices {
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
}

/* Plan Block */

.block-plans .grid {
  gap: 8rem 4rem;
}
.block-plans .card {
  max-width: 500px;
  margin: 0 auto;
}

/* Features Block */

.feature {
  gap: 4rem 2rem;
  margin: 12rem 0;
}
.feature:first-of-type {
  margin-top: 6rem;
}
.block--dark .feature__heading {
  color: white;
}
.feature__heading {
  margin: 1rem 0;
}
.feature__image {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .feature:nth-of-type(even) .feature__content {
    order: 2;
  }
}

/* Showcase Block */

.block-showcase__image {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .block-showcase .grid {
    grid-template-columns: 50% 50%;
  }
  .block-showcase__image {
    width: auto;
    max-width: 700px;
  }
  .block-showcase__picture {
    justify-self: end;
  }
}

/* Footer */
.footer {
  background: #232323;
}
.footer__item {
  padding: 2rem;
  border-bottom: 1px solid #393939;
}
.footer__heading {
  text-transform: uppercase;
  font-weight: 600;
}

.footer__content {
  font-size: 1.6rem;
  margin: 0;
}

.footer .grid {
  gap: 2rem 1rem;
}
.footer-list__item {
  margin: 1.1rem 0;
}
.footer-list__item > a {
  color: #ccc;
  opacity: 0.6;
  transition: color 0.3s;
}
.footer-list__item > a:hover {
  color: white;
  opacity: 1;
}
.footer-logo__container {
  margin-top: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.footer__copyright {
  font-size: 2.1rem;
  color: white;
  opacity: 0.3;
}

@media screen and (min-width: 768px) {
  .footer__sections {
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
  .footer-logo__container {
    order: -1;
    margin-top: 1rem;
    justify-content: flex-start;
  }
  .footer-logo__container img {
    /* TODO: Consider changing all images to 100% width */
    width: 100%;
    max-width: 400px;
  }
  .footer__copyright {
    font-size: 1.5rem;
  }
  .footer .collapsible__chevron {
    display: none;
  }
  .footer__content {
    opacity: 1;
    max-height: 100vh;
  }
  .footer__item {
    border-bottom: none;
  }
  .footer__heading {
    font-size: 1.6rem;
  }
}
